"""
FASTA
"""
function read_fasta(fasta)
    sequences = Dict{String, String}()
    seq_strings = split(fasta, '>')
    popfirst!(seq_strings)
    for i in seq_strings
        seq = split(i, "\n")
        sequences[seq[1]] = seq[2]
    end
    return sequences
end

